svg.script:
function getParameterByName(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, "\\$&");
    var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
        results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, " "));
}

var title = getParameterByName('title');
var description = getParameterByName('desc');
var bgColor = "#"+ getParameterByName('bgColor');
var strokeColor = "#"+ getParameterByName('strkColor');
var textColor = "#"+ getParameterByName('txtColor');

document.getElementById("title").innerHTML = title == null ? "undefined" : title;
document.getElementById("description").innerHTML = description == null ? "undefined" : description;
document.getElementById("postit").style.fill = bgColor == null ? "#ffffcc" : bgColor;
document.getElementById("postit").style.stroke = strokeColor == null ? "#ffbf00" : strokeColor;
document.getElementById("title").style.fill = textColor == null ? "#000000" : textColor;
document.getElementById("description").style.color = textColor == null ? "#000000" : textColor;
